/*****************************************************************************
 枚举名    : NAS_MMC_SYSCFG_STA_ENUM
 枚举说明  : SYSCFG状态机状态枚举定义
 1.日    期   : 2011年7月2日
   作    者   : z00161729
   修改内容   : 新生成函数
*****************************************************************************/
enum NAS_MMC_SYSCFG_STA_ENUM
{
    /* SYSCFG状态机初始化状态 */
    NAS_MMC_SYSCFG_STA_INIT                                    = 0,

    /*SYSCFG过程，等待GAS的RRMM_SYS_CFG_CNF设置回复消息 */
    NAS_MMC_SYSCFG_STA_WAIT_GAS_SYSCFG_CNF                     = 1,

    /*SYSCFG过程，等待WAS的RRMM_SYS_CFG_CNF设置回复消息 */
    NAS_MMC_SYSCFG_STA_WAIT_WAS_SYSCFG_CNF                     = 2,

    /*SYSCFG过程，等待GU链接释放 */
    NAS_MMC_SYSCFG_STA_WAIT_RRC_CONN_REL_IND                   = 3,

#if (FEATURE_ON == FEATURE_LTE)
    /*SYSCFG过程，等待LMM的ID_LMM_MMC_SYS_CFG_CNF设置回复消息 */
    NAS_MMC_SYSCFG_STA_WAIT_LMM_SYSCFG_CNF                     = 4,

    /*SYSCFG过程，EPS域的链接释放结果 */
    NAS_MMC_SYSCFG_STA_WAIT_EPS_CONN_REL_IND                   = 5,
#endif

    NAS_MMC_SYSCFG_STA_BUTT
};

typedef VOS_UINT32  NAS_MMC_SYSCFG_STA_ENUM_UINT32;


